import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      // For testing until backend is ready
      if (username === "admin" && password === "1234") {
        localStorage.setItem("loggedIn", "true");
        navigate("/dashboard");
        return;
      }

      const response = await axios.post("http://localhost:8081/api/auth/login", { username, password });
      if (response.data === "success") {
        localStorage.setItem("loggedIn", "true");
        navigate("/dashboard");
      } else {
        alert("Invalid username or password");
      }
    } catch (err) {
      console.error('Login error:', err);
      if (err.code === "ERR_NETWORK") {
        alert("Cannot connect to backend server. Please ensure the backend is running on port 8081");
      } else {
        alert(`Server error: ${err.response?.data?.message || err.message}`);
      }
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100 bg-light">
      <div className="card p-5 shadow" style={{ width: "350px" }}>
        {/* You can replace this with a logo */}
        <h3 className="text-center mb-4">üè¢ Company Name</h3>

        <form onSubmit={handleLogin}>
          <div className="mb-3">
            <label>Username</label>
            <input type="text"
              className="form-control"
              onChange={(e) => setUsername(e.target.value)}
              required />
          </div>

          <div className="mb-3">
            <label>Password</label>
            <input type="password"
              className="form-control"
              onChange={(e) => setPassword(e.target.value)}
              required />
          </div>

          <button type="submit" className="btn btn-primary w-100">Login</button>
        </form>
      </div>
    </div>
  );
}

export default LoginPage;
